import React from "react";
import "../styles/LoginOptions.css";

const LoginOptions = () => {
  return (
    <div className="login-options">
      <h1>Choose Your Role</h1>
      <div className="options-container">
        <div className="option-card">
          <h2>Admin</h2>
          <p>
            Login as an administrator to access the dashboard to manage app
            data.
          </p>
        </div>
        <div className="option-card">
          <h2>Student</h2>
          <p>Login as a student to explore course materials and assignments.</p>
        </div>
        <div className="option-card">
          <h2>Teacher</h2>
          <p>
            Login as a teacher to create courses, assignments, and track student
            progress.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginOptions;
