import React from "react";
import "../styles/LoginOptions.css";
import { useNavigate } from "react-router-dom";

const LoginOptions = () => {
  const navigate = useNavigate();

  return (
    <div className="login-options">
      <h1>School Management System</h1>
      <div className="options-container">
        <div className="option-card" onClick={() => navigate("/admin")}>
          <i className="fa fa-user-shield"></i>
          <h2>Admin</h2>
          <p>Login as an administrator to access the dashboard to manage app data.</p>
        </div>
        <div className="option-card" onClick={() => navigate("/student")}>
          <i className="fa fa-graduation-cap"></i>
          <h2>Student</h2>
          <p>Login as a student to explore course materials and assignments.</p>
        </div>
        <div className="option-card" onClick={() => navigate("/teacher")}>
          <i className="fa fa-chalkboard-teacher"></i>
          <h2>Teacher</h2>
          <p>Login as a teacher to create courses, assignments, and track student progress.</p>
        </div>
      </div>
    </div>
  );
};

export default LoginOptions;
