import React, { useState } from "react";
import "../styles/AdminLogin.css";

const ResetPassword = () => {
  const [email, setEmail] = useState(
    localStorage.getItem("emailToReset") || "" // Retrieve email passed from Forgot Password
  );
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleResetPassword = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (newPassword !== confirmPassword) {
      setError("Passwords do not match.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/reset-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, newPassword }),
      });

      const data = await response.json();

      if (response.status === 200) {
        setSuccess(data.message); // Display success message
        setTimeout(() => {
          window.location.href = "/adminlogin"; // Redirect after successful password reset
        }, 2000);
      } else {
        setError(data.error); // Display error message
      }
    } catch (error) {
      console.error("Error:", error);
      setError("Something went wrong. Please try again.");
    }
  };

  return (
    <div className="admin-login-container">
      <h1>Reset Password</h1>
      <p>Set a new password for your account</p>
      <form className="login-form" onSubmit={handleResetPassword}>
        <div className="input-group">
          <label htmlFor="email">Your Email *</label>
          <input
            type="email"
            id="email"
            value={email}
            disabled // Prevent modification
            style={{ backgroundColor: "#f0f0f0", cursor: "not-allowed" }}
          />
        </div>
        <div className="input-group">
          <label htmlFor="newPassword">New Password *</label>
          <input
            type="password"
            id="newPassword"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="Enter new password"
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="confirmPassword">Confirm Password *</label>
          <input
            type="password"
            id="confirmPassword"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm new password"
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        {success && <p className="success">{success}</p>}
        <button type="submit" className="login-btn">
          Reset Password
        </button>
      </form>
    </div>
  );
};

export default ResetPassword;
